import 'dart:convert';import 'package:hive_flutter/adapters.dart';import 'package:test_app/app_models/user_model/user_model.dart';import 'package:test_app/src/utils/app_utils/app_utils.dart';class HiveUtils {  static final box = Hive.box('TestAppBox');  static String isRegisterdKey = "IS_REGISTERD_KEY";  static String userModelKey = "USER_MODEL_KEY";  static void setUserModel(UserModel userModel){    String encodedUserModel = json.encode(userModel);    box.put(userModelKey, encodedUserModel);  } static Future<void> getUserModel() async {   String encodedUserModel = await box.get(userModelKey);      dynamic decodeUserModel = json.decode(encodedUserModel);    AppUtils.userModel =  UserModel.fromJson(decodeUserModel);  }}