import 'package:flutter/cupertino.dart';import 'package:test_app/src/widgets/app_text_widgets/small_text.dart';import '../../constants/app_const/app_text_const.dart';import '../custom_button/custom_button.dart';class AppExceptionView extends StatelessWidget {  final String message;  final String authName;  final void Function()? onPressed;  const AppExceptionView({super.key, required this.message, required this.authName, this.onPressed});  @override  Widget build(BuildContext context) {    return Column(      children: [        const SizedBox(height: 250),        SmallText(text: message,        textAlign: TextAlign.center,        ),        const SizedBox(height: 30),        CustomButton(            width: 343,            height: 64,            isLoading: false,            onPressed: onPressed ?? (){},            title: TextConst.toRetry)      ],    );  }}