import 'package:flutter/material.dart';import 'package:google_fonts/google_fonts.dart';import 'package:test_app/src/constants/app_const/colors_const.dart';import '../../constants/app_const/font_and_size_const.dart';class TextFiledWidget extends StatelessWidget {  final String lableName;  final String? errorMessage;  final TextInputType? type;  final dynamic Function(String?)? callback;  final TextEditingController textEditingController;  const TextFiledWidget(      {Key? key,        required this.lableName,        this.errorMessage,        this.type,        this.callback,        required this.textEditingController,      })      : super(key: key);  @override  Widget build(BuildContext context) {    return _buildSearchBar(context);  }  Widget _buildSearchBar(BuildContext context) {    return  TextFormField(      keyboardType: type,      onChanged: callback,      controller: textEditingController,      cursorColor: ColorConst.appButtonColor,      decoration: InputDecoration(        contentPadding: const EdgeInsets.all(16),        hintText: lableName,        errorText: errorMessage,        errorStyle: GoogleFonts.manrope(          color: ColorConst.appRedColor,          fontSize: SizeConst.kExtraSmallFont8,          fontWeight: WeightsConst.kSmallWeight400,        ),        filled: true,        fillColor: ColorConst.whiteColor,        hintStyle: GoogleFonts.manrope(          color: ColorConst.appLableColor,          fontSize: SizeConst.kMediumFont16,          fontWeight: WeightsConst.kSmallWeight400,        ),        counterStyle:  GoogleFonts.manrope(          color: ColorConst.whiteColor        ),        errorBorder: const OutlineInputBorder(          borderSide:  BorderSide(color: ColorConst.appRedColor, width: 0.6),        ),        border: const OutlineInputBorder(),        focusedBorder: const OutlineInputBorder(          borderSide: BorderSide(color: Colors.transparent,width: 0.6),        ),        enabledBorder: const UnderlineInputBorder(          borderSide: BorderSide(color: Colors.transparent,width: 0.6),        ),      ),    );  }}