import 'package:flutter/material.dart';import 'package:google_fonts/google_fonts.dart';import 'package:test_app/src/constants/colors_const.dart';import '../../constants/font_and_size_const.dart';class TextFiledWidget extends StatelessWidget {  final String lableName;  final double elevation;  final TextInputType? type;  final TextEditingController textEditingController;  const TextFiledWidget(      {Key? key,        required this.lableName,        this.elevation = 10,        this.type,        required this.textEditingController,      })      : super(key: key);  @override  Widget build(BuildContext context) {    return _buildSearchBar(context);  }  Widget _buildSearchBar(BuildContext context) {    return  TextFormField(      keyboardType: type,      controller: textEditingController,      cursorColor: ColorConst.appLableColor,      decoration: InputDecoration(        hintText: lableName,        filled: true,        fillColor: ColorConst.whiteColor,        hintStyle: GoogleFonts.manrope(          textStyle: Theme.of(context).textTheme.bodyMedium,          fontStyle: FontStyle.normal,          color: ColorConst.appLableColor,          fontSize: SizeConst.kMediumFont16,          fontWeight: WeightsConst.kSmallWeight400,        ),        counterStyle:  GoogleFonts.lato(          textStyle: Theme.of(context).textTheme.bodyMedium,          fontStyle: FontStyle.normal,          color: ColorConst.whiteColor        ),        border: const OutlineInputBorder(),        focusedBorder: const OutlineInputBorder(          borderSide: BorderSide(color: Colors.transparent),        ),        enabledBorder: const UnderlineInputBorder(          borderSide: BorderSide(color: Colors.transparent),        ),      ),    );  }}