import 'package:flutter/material.dart';import 'package:google_fonts/google_fonts.dart';import 'package:test_app/src/constants/colors_const.dart';import 'package:test_app/src/widgets/app_text_widgets/big_text.dart';import 'package:test_app/views/profile_view/profile_page.dart';import '../../src/constants/font_and_size_const.dart';import '../../src/controllers/enter_number_cont.dart';import '../../src/widgets/app_text_widgets/small_text.dart';class MainPage extends StatefulWidget {  final AppStreamController controller;  const MainPage({super.key, required this.controller});  @override  State<MainPage> createState() => _MainPageState();}class _MainPageState extends State<MainPage> {  int _selectedTab = 0;  late List<Widget> pages = [    const Center(      child: SmallText(text: "Лента")    ),    const Center(      child: SmallText(text: "Карта")    ),    const Center(      child: SmallText(text: "Избранные")    ),    UserProfile(controller: widget.controller),  ];  _changeTab(int index) {    setState(() {      _selectedTab = index;    });  }  @override  Widget build(BuildContext context) {    return  Scaffold(      backgroundColor: ColorConst.appBackgroundColor,      body: pages[_selectedTab],      bottomNavigationBar: BottomNavigationBar(        currentIndex: _selectedTab,        showUnselectedLabels: true,        onTap: (index) => _changeTab(index),        selectedItemColor: ColorConst.appButtonColor,        unselectedItemColor: ColorConst.bleackColor,        unselectedLabelStyle: GoogleFonts.manrope(          fontSize: 10,          fontWeight: WeightsConst.kSmallWeight400,          fontStyle: FontStyle.normal,          color: ColorConst.bleackColor,        ),        selectedLabelStyle: GoogleFonts.manrope(          fontSize: 10,          fontWeight: WeightsConst.kSmallWeight400,          fontStyle: FontStyle.normal,          color: ColorConst.bleackColor,        ),        items:  [          BottomNavigationBarItem(icon: Image.asset("assets/lenta.png",            color: _selectedTab == 0 ? ColorConst.appButtonColor : ColorConst.bleackColor,            width: 18.18,height: 18,), label: "Лента"),          BottomNavigationBarItem(icon: Image.asset("assets/carta.png",            color: _selectedTab == 1 ? ColorConst.appButtonColor : ColorConst.bleackColor,            width: 18.18,height: 18,), label: "Карта"),          BottomNavigationBarItem(icon: Image.asset("assets/izible.png",            color: _selectedTab == 2 ? ColorConst.appButtonColor : ColorConst.bleackColor,            width: 18.18,height: 18,), label: "Избранные"),          BottomNavigationBarItem(icon: Image.asset("assets/profile.png",            color: _selectedTab == 3 ? ColorConst.appButtonColor : ColorConst.bleackColor,            width: 18.18,height: 18,), label: "Профиль"),        ],      ),    );  }}