import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:google_fonts/google_fonts.dart';import 'package:test_app/src/constants/app_const/colors_const.dart';import 'package:test_app/views/profile_view/profile_page.dart';import '../../cubits/bottom_bar_cubit/navbar_cubit.dart';import '../../src/constants/app_const/app_text_const.dart';import '../../src/constants/app_const/font_and_size_const.dart';import '../../src/controllers/stream_controller.dart';import '../card_view/card_view.dart';import '../favorites_view/favorites_page.dart';import '../slow_view/slow_page.dart';class MainPage extends StatefulWidget {  final AppStreamController controller;  const MainPage({super.key, required this.controller});  @override  State<MainPage> createState() => _MainPageState();}class _MainPageState extends State<MainPage> {  late List<Widget> pages = [    const SlowPage(),    const CardPage(),    const FavoritesPage(),    UserProfile(controller: widget.controller),  ];  @override  Widget build(BuildContext context) {    return BlocProvider(      create: (context) => NavbarCubit(),      child: BlocConsumer<NavbarCubit, NavbarState>(        listener: (context, state) {},        builder: (context, state) {          var cubit = NavbarCubit.get(context);          return Scaffold(            backgroundColor: ColorConst.appBackgroundColor,            bottomNavigationBar: BottomNavigationBar(              currentIndex: cubit.currentIndex,              showUnselectedLabels: true,              onTap: (index) {                cubit.changeBottomNavBar(index);              },              selectedItemColor: ColorConst.appButtonColor,              unselectedItemColor: ColorConst.bleackColor,              unselectedLabelStyle: GoogleFonts.manrope(                fontSize: 10,                fontWeight: WeightsConst.kSmallWeight400,                fontStyle: FontStyle.normal,                color: ColorConst.bleackColor,              ),              selectedLabelStyle: GoogleFonts.manrope(                fontSize: 10,                fontWeight: WeightsConst.kSmallWeight400,                fontStyle: FontStyle.normal,                color: ColorConst.bleackColor,              ),              items:  [                BottomNavigationBarItem(icon: Image.asset("assets/lenta.png",                  color: cubit.currentIndex == 0 ? ColorConst.appButtonColor : ColorConst.bleackColor,                  width: 18.18,height: 18,), label: TextConst.slowText),                BottomNavigationBarItem(icon: Image.asset("assets/carta.png",                  color: cubit.currentIndex == 1 ? ColorConst.appButtonColor : ColorConst.bleackColor,                  width: 18.18,height: 18,), label: TextConst.cardText),                BottomNavigationBarItem(icon: Image.asset("assets/izible.png",                  color: cubit.currentIndex == 2 ? ColorConst.appButtonColor : ColorConst.bleackColor,                  width: 18.18,height: 18,), label: TextConst.favoritesText),                BottomNavigationBarItem(icon: Image.asset("assets/profile.png",                  color: cubit.currentIndex == 3 ? ColorConst.appButtonColor : ColorConst.bleackColor,                  width: 18.18,height: 18,), label: TextConst.profileText),              ],            ),            body: pages[cubit.currentIndex],          );        },      ),    );  }}