import 'package:flutter/material.dart';import 'package:test_app/service/routes/routes_name.dart';import 'package:test_app/src/constants/colors_const.dart';import 'package:test_app/src/controllers/enter_number_cont.dart';import 'package:test_app/src/utils/app_utils.dart';import 'package:test_app/src/widgets/app_text_widgets/big_text.dart';import '../../src/constants/font_and_size_const.dart';import '../../src/widgets/app_text_widgets/small_text.dart';class UserProfile extends StatelessWidget {  final AppStreamController controller;  const UserProfile({super.key, required this.controller});  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: ColorConst.appBackgroundColor,      appBar: AppBar(        centerTitle: true,        title: SmallText(text: "Профиль", color: ColorConst.bleackColor),      ),      body: buildBody(context),    );  }  Widget buildBody(BuildContext context){    return Column(      crossAxisAlignment: CrossAxisAlignment.center,      children: [        const SizedBox(height: 64),       const Icon(Icons.account_circle_outlined,size: 100),        BigText(text: AppUtils.userModel.user?.nickname ?? "",            fontWidget: WeightsConst.kMediumWeight600,            size: 24),        const SizedBox(height: 12),        SmallText(text: AppUtils.userModel.user?.email ?? "",        color: ColorConst.appEmileTextColor,          fontWidget: WeightsConst.kSmallWeight400,        ),        const SizedBox(height: 12),        GestureDetector(          onTap: (){            Navigator.pushReplacementNamed(context, MainRoutes.AuthPage);          },          child:  Container(              color: ColorConst.whiteColor,              width: double.infinity,              height: 55,              child: Padding(                padding: const EdgeInsets.only(left: 45, top: 12),                child: SmallText(text: "Выйти",color: ColorConst.appRedColor,                    fontWidget: WeightsConst.kSmallWeight400,                    size: SizeConst.kMediumFont16),              )          ),        )      ],    );  }}